+  const data: OmitForCapture<ILinkedInPost> = {
+    text,
+    label: "",
+    contentType: NodeType.LINKEDIN_POST,
+    body: {
+      postedAt
+    },
+    metadata: {
+      postId: postId ?? "unknown",
+      username,
+      links,
+      headline: jobTitle,
+      postedAt: postedAtStr ?? ""
+    },
+    url: postUrl
