+    const rect = overlayContainer.getBoundingClientRect();
+    const x = event.clientX - rect.left;
+    const y = event.clientY - rect.top;
+
+    // Calculate relative position for scaling
+    const relativeX = canvasWidth > 0 ? x / canvasWidth : 0;
+    const relativeY = canvasHeight > 0 ? y / canvasHeight : 0;
+
+    const newAnnotation: TextAnnotation = {
+      id: generateId(),
+      pageNumber: $pdfState.currentPage,
+      x,
+      y,
+      text: '',
+      fontSize: 16,
+      color: $drawingState.color,
+      fontFamily: 'Arial, sans-serif',
+      relativeX,
+      relativeY
+    };
+
+    addTextAnnotation(newAnnotation);
+    startEditing(newAnnotation);
+  }
+
+  // Start editing an annotation
+  async function startEditing(annotation: TextAnnotation) {
+    editingAnnotation = annotation;
+    await tick(); // Wait for DOM update
+    if (editInput) {
+      editInput.focus();
+      editInput.select();
+    }
+  }
+
+  // Save editing changes
+  function saveEdit() {
+    if (!editingAnnotation || !editInput) return;
+
+    const newText = editInput.value.trim();
+    
+    if (newText === '') {
+      // Delete empty annotation
+      deleteTextAnnotation(editingAnnotation.id, editingAnnotation.pageNumber);
+    } else {
+      // Update annotation with new text
+      updateTextAnnotation({
+        ...editingAnnotation,
+        text: newText
+      });
+    }
+    
+    editingAnnotation = null;
+  }
+
+  // Cancel editing
+  function cancelEdit() {
+    if (!editingAnnotation) return;
+    
+    // If annotation was newly created and empty, delete it
+    if (editingAnnotation.text === '') {
+      deleteTextAnnotation(editingAnnotation.id, editingAnnotation.pageNumber);
+    }
+    
+    editingAnnotation = null;
+  }
+
+  // Handle keyboard events in edit mode
+  function handleKeyDown(event: KeyboardEvent) {
+    if (event.key === 'Enter' && !event.shiftKey) {
+      event.preventDefault();
+      saveEdit();
+    } else if (event.key === 'Escape') {
+      event.preventDefault();
+      cancelEdit();
+    }
+  }
+
+  // Calculate absolute position from relative position (for scaling)
+  function getAbsolutePosition(annotation: TextAnnotation) {
+    return {
+      x: annotation.relativeX * canvasWidth,
+      y: annotation.relativeY * canvasHeight
+    };
+  }
+
+  // Update relative position when annotation is moved
+  function updateRelativePosition(annotation: TextAnnotation, newX: number, newY: number) {
+    const relativeX = canvasWidth > 0 ? newX / canvasWidth : annotation.relativeX;
+    const relativeY = canvasHeight > 0 ? newY / canvasHeight : annotation.relativeY;
+    
+    updateTextAnnotation({
+      ...annotation,
+      x: newX,
+      y: newY,
+      relativeX,
+      relativeY
+    });
+  }
+
+  // Handle double-click to edit existing annotation
+  function handleAnnotationDoubleClick(annotation: TextAnnotation) {
+    startEditing(annotation);
+  }
+
+  // Handle annotation drag
+  let draggedAnnotation: TextAnnotation | null = null;
+  let dragStart = { x: 0, y: 0 };
+  let annotationStart = { x: 0, y: 0 };
+
+  function handleMouseDown(event: MouseEvent, annotation: TextAnnotation) {
+    // Don't start drag if we're editing
+    if (editingAnnotation) return;
+    
+    draggedAnnotation = annotation;
+    dragStart = { x: event.clientX, y: event.clientY };
+    const pos = getAbsolutePosition(annotation);
+    annotationStart = { x: pos.x, y: pos.y };
+    
+    event.preventDefault();
+  }
+
+  function handleMouseMove(event: MouseEvent) {
+    if (!draggedAnnotation) return;
+    
+    const deltaX = event.clientX - dragStart.x;
+    const deltaY = event.clientY - dragStart.y;
+    
+    const newX = Math.max(0, Math.min(canvasWidth - 100, annotationStart.x + deltaX));
+    const newY = Math.max(0, Math.min(canvasHeight - 20, annotationStart.y + deltaY));
+    
+    updateRelativePosition(draggedAnnotation, newX, newY);
+  }

