+            assert_eq!(result.cmds.len(), 1);
+            
+            match &result.cmds[0] {
+                Command::Wait { secs } => {
+                    assert_eq!(*secs, expected_duration, "Duration mismatch for input: {}", input);
+                }
+                _ => panic!("Expected Wait command for input: {}", input),
+            }
+        }
+    }

