-          .map(dir => path.join(projectsDir, dir))
-          .filter(dir => fs.statSync(dir).isDirectory());
+        const projectDirs = fs.readdirSync(projectsDir, { withFileTypes: true })
+          .filter(d => d.isDirectory())
